import React, { useState, useRef, useEffect } from "react";
import "./App.css";
import { Stage, Layer, Rect, Transformer } from "react-konva";

function App() {
  const containerRef = useRef();
  const [dimensions, setDimensions] = useState({
    width: 0,
    height: 0,
  });

  const [rectangles, setRectangles] = useState([]);
  const [drawing, setDrawing] = useState(false);
  const [startPos, setStartPos] = useState({ x: 0, y: 0 });
  const [endPos, setEndPos] = useState({ x: 0, y: 0 });

  const handleSelectShape = (e, index) => {
    setSelectedShapeIndex(index);
  };
  const handleDragEnd = (e, index) => {
    const newRectangles = [...rectangles];
    newRectangles[index] = {
      ...newRectangles[index],
      x: e.target.x(),
      y: e.target.y(),
    };
    setRectangles(newRectangles);
  };

  // set dimensions
  useEffect(() => {
    const container = containerRef.current;
    setDimensions({
      width: 1120,
      height: 630,
    });
  }, []);

  // actions
  const handleMouseDown = (e) => {
    setDrawing(true);
    const stage = e.target.getStage();
    setStartPos(stage.getPointerPosition());
    setEndPos(stage.getPointerPosition());
  };
  const handleMouseMove = (e) => {
    if (!drawing) return;
    const stage = e.target.getStage();
    setEndPos(stage.getPointerPosition());
  };
  const handleMouseUp = () => {
    if (!drawing) return;
    setDrawing(false);
    const newRectangle = {
      x: Math.min(startPos.x, endPos.x),
      y: Math.min(startPos.y, endPos.y),
      width: Math.abs(endPos.x - startPos.x),
      height: Math.abs(endPos.y - startPos.y),
      fill: "blue", // Rectangle fill color
    };
    setRectangles([...rectangles, newRectangle]);
  };

  return (
    <div className="App">
      <div className="global-container">
        <div className="video-container">
          <video src="/video.mp4" autoPlay muted loop />
        </div>
        <div className="canvas-container" ref={containerRef}>
          <Stage
            onMouseDown={handleMouseDown}
            onMouseMove={handleMouseMove}
            onMouseUp={handleMouseUp}
            width={dimensions.width}
            height={dimensions.height}
          >
            <Layer>
              {rectangles.map((rect, index) => (
                <Rect
                  key={index}
                  x={rect.x}
                  y={rect.y}
                  width={rect.width}
                  height={rect.height}
                  fill={rect.fill}
                  draggable
                  onClick={(e) => handleSelectShape(e, index)}
                  onDragEnd={(e) => handleDragEnd(e, index)}
                />
              ))}
            </Layer>
            <Layer>
              {drawing && (
                <Rect
                  x={Math.min(startPos.x, endPos.x)}
                  y={Math.min(startPos.y, endPos.y)}
                  width={Math.abs(endPos.x - startPos.x)}
                  height={Math.abs(endPos.y - startPos.y)}
                  stroke="blue"
                />
              )}
            </Layer>
          </Stage>
        </div>
      </div>
    </div>
  );
}

export default App;
