import React, { useState, useRef, useEffect } from "react";
import { Stage, Layer, Rect, Text, Image } from "react-konva";
import { useNavigate } from "react-router";
import { getDownloadURL, ref } from "firebase/storage";
import { storage } from "../firebase";
import Video from "../components/Video";

const Edit = ({ fileName }) => {
  const navigate = useNavigate();
  const containerRef = useRef();

  const [tool, setTool] = useState("rect");
  const [videoURL, setVideoURL] = useState(null);

  const [dimensions, setDimensions] = useState({
    width: 0,
    height: 0,
  });
  const [rectangles, setRectangles] = useState([]);
  const [textElements, setTextElements] = useState([]);

  const [drawing, setDrawing] = useState(false);
  const [activeTextId, setActiveTextId] = useState(null);
  const [startPos, setStartPos] = useState({ x: 0, y: 0 });
  const [endPos, setEndPos] = useState({ x: 0, y: 0 });

  const handleDragEnd = (e, index) => {
    const newRectangles = [...rectangles];
    newRectangles[index] = {
      ...newRectangles[index],
      x: e.target.x(),
      y: e.target.y(),
    };
    setRectangles(newRectangles);
  };

  // set dimensions
  useEffect(() => {
    const containerWidth = containerRef.current.offsetWidth;

    setDimensions({
      width: containerWidth,
      height: 600,
    });
  }, []);

  // actions
  const handleMouseDown = (e) => {
    if (tool === "rect") {
      setDrawing(true);
      const stage = e.target.getStage();
      setStartPos(stage.getPointerPosition());
      setEndPos(stage.getPointerPosition());
    } else if (tool === "text") {
      const { x, y } = e.target.getStage().getPointerPosition();
      const newText = {
        x,
        y,
        text: "Double-click to edit",
        fontSize: 18,
        fill: "black",
      };
      setTextElements([...textElements, newText]);
    }
  };
  const handleMouseMove = (e) => {
    if (!drawing) return;
    const stage = e.target.getStage();
    setEndPos(stage.getPointerPosition());
  };
  const handleMouseUp = () => {
    if (!drawing) return;
    setDrawing(false);
    const newRectangle = {
      x: Math.min(startPos.x, endPos.x),
      y: Math.min(startPos.y, endPos.y),
      width: Math.abs(endPos.x - startPos.x),
      height: Math.abs(endPos.y - startPos.y),
      fill: "blue", // Rectangle fill color
    };
    setRectangles([...rectangles, newRectangle]);
  };

  // save to json server
  const saveShapesHandler = () => {
    const shapes = [...rectangles, ...textElements];
    fetch("http://localhost:3000/shapes", {
      method: "POST",
      body: JSON.stringify(shapes),
      headers: { "Content-type": "application/json" },
    })
      .then((res) => res.json())
      .then((data) => console.log(data));
  };

  //getting video for preview
  React.useEffect(() => {
    if (fileName) {
      const videoBlob = new Blob([fileName]);
      const videoObjectURL = URL.createObjectURL(videoBlob);
      setVideoURL(videoObjectURL);
    }
  }, [fileName]);

  return (
    <div className="App">
      <div className="global-container">
        <div className="canvas-container" ref={containerRef}>
          <Stage
            onMouseDown={handleMouseDown}
            onMouseMove={handleMouseMove}
            onMouseUp={handleMouseUp}
            width={dimensions.width}
            height={dimensions.height}
            className="canvas"
          >
            <Video src={videoURL} containerRef={containerRef} />
            <Layer>
              {rectangles.map((rect, index) => (
                <Rect
                  key={index}
                  x={rect.x}
                  y={rect.y}
                  width={rect.width}
                  height={rect.height}
                  fill={rect.fill}
                  draggable
                  onDragEnd={(e) => handleDragEnd(e, index)}
                />
              ))}
            </Layer>
            <Layer>
              {drawing && (
                <Rect
                  x={Math.min(startPos.x, endPos.x)}
                  y={Math.min(startPos.y, endPos.y)}
                  width={Math.abs(endPos.x - startPos.x)}
                  height={Math.abs(endPos.y - startPos.y)}
                  stroke="blue"
                />
              )}
            </Layer>
            <Layer></Layer>
          </Stage>
        </div>
      </div>
      <div>
        <button onClick={() => setTool("rect")}>Rectangle</button>
        <button onClick={() => setTool("text")}>Text</button>
        <button onClick={saveShapesHandler}>Save</button>
        <button onClick={() => navigate("/preview")}>Preview</button>
      </div>
    </div>
  );
};

export default Edit;
