import React, { useState, useRef, useEffect } from "react";
import { Stage, Layer, Rect, Text, Image } from "react-konva";

const TextElements = () => {
  const [textElements, setTextElements] = useState([]);
  const [selectedText, setSelectedText] = useState(null);

  const handleCanvasClick = (e) => {
    // Check if the click is on a text element, if yes, enable editing
    const clickedText = e.target;
    if (clickedText instanceof window.Konva.Text) {
      setSelectedText(clickedText);
    } else {
      // Otherwise, create a new text element
      const newText = (
        <Text
          key={textElements.length}
          x={
            e.evt.clientX -
            e.target.getStage().container().getBoundingClientRect().left
          }
          y={
            e.evt.clientY -
            e.target.getStage().container().getBoundingClientRect().top
          }
          text="Click to edit"
          draggable
          onClick={() => setSelectedText(newText)}
          onTransform={() => setSelectedText(newText)}
          onTap={() => setSelectedText(newText)}
        />
      );
      setTextElements([...textElements, newText]);
    }
  };

  return <Layer></Layer>;
};

export default TextElements;
