import React, { useState, useRef, useEffect } from "react";
import "./App.css";
import { Stage, Layer, Rect, Text } from "react-konva";
import { Route, Routes } from "react-router";
import { useNavigate } from "react-router";

function App() {
  const navigate = useNavigate();
  const containerRef = useRef();

  const [tool, setTool] = useState("rect");

  const [dimensions, setDimensions] = useState({
    width: 0,
    height: 0,
  });
  const [rectangles, setRectangles] = useState([]);
  const [textElements, setTextElements] = useState([]);

  const [drawing, setDrawing] = useState(false);
  const [startPos, setStartPos] = useState({ x: 0, y: 0 });
  const [endPos, setEndPos] = useState({ x: 0, y: 0 });

  const handleDragEnd = (e, index) => {
    const newRectangles = [...rectangles];
    newRectangles[index] = {
      ...newRectangles[index],
      x: e.target.x(),
      y: e.target.y(),
    };
    setRectangles(newRectangles);
  };

  // set dimensions
  useEffect(() => {
    // const container = containerRef.current;
    setDimensions({
      width: 1120,
      height: 630,
    });
  }, []);

  // actions
  const handleMouseDown = (e) => {
    if (tool === "rect") {
      setDrawing(true);
      const stage = e.target.getStage();
      setStartPos(stage.getPointerPosition());
      setEndPos(stage.getPointerPosition());
    } else if (tool === "text") {
      const { x, y } = e.target.getStage().getPointerPosition();
      const newText = {
        x,
        y,
        text: "Double-click to edit",
        fontSize: 18,
        fill: "black",
      };
      setTextElements([...textElements, newText]);
    }
  };
  const handleMouseMove = (e) => {
    if (!drawing) return;
    const stage = e.target.getStage();
    setEndPos(stage.getPointerPosition());
  };
  const handleMouseUp = () => {
    if (!drawing) return;
    setDrawing(false);
    const newRectangle = {
      x: Math.min(startPos.x, endPos.x),
      y: Math.min(startPos.y, endPos.y),
      width: Math.abs(endPos.x - startPos.x),
      height: Math.abs(endPos.y - startPos.y),
      fill: "blue", // Rectangle fill color
    };
    setRectangles([...rectangles, newRectangle]);
  };

  const [editingIndex, setEditingIndex] = useState(null);

  const handleTextClick = (index) => {
    setEditingIndex(index);
  };

  const handleTextEditing = (e) => {
    const updatedTextElements = [...textElements];
    updatedTextElements[editingIndex].text = e.target.value;
    setTextElements(updatedTextElements);
  };

  const handleTextSave = () => {
    setEditingIndex(null); // Stop editing
  };

  const renderEditableText = () => {
    if (editingIndex !== null) {
      const editedText = textElements[editingIndex].text;
      return (
        <input
          type="text"
          value={editedText}
          onChange={handleTextEditing}
          onBlur={handleTextSave}
          autoFocus
        />
      );
    }
    return null;
  };

  const handleTextDrag = (index, e) => {
    const updatedTextElements = [...textElements];
    updatedTextElements[index] = {
      ...updatedTextElements[index],
      x: e.target.x(),
      y: e.target.y(),
    };
    setTextElements(updatedTextElements);
  };

  const saveShapesHandler = () => {
    const shapes = [...rectangles, ...textElements];
    fetch("http://localhost:3000/shapes", {
      method: "POST",
      body: JSON.stringify(shapes),
      headers: { "Content-type": "application/json" },
    })
      .then((res) => res.json())
      .then((data) => console.log(data));
  };

  const loadDataHandler = () => {
    const shapes = [...rectangles, ...textElements];
    fetch("http://localhost:3000/shapes")
      .then((res) => res.json())
      .then((data) => {
        setRectangles(data[0]);
        setTextElements(data[0]);
      });
  };

  return (
    <>
      <Routes>
        <Route path="/" element={<div></div>} />
        <Route
          path="/preview"
          element={
            <div className="App">
              <div className="global-container">
                <div className="video-container">
                  <video src="/video.mp4" autoPlay muted loop />
                </div>
                <div className="canvas-container" ref={containerRef}>
                  <Stage width={dimensions.width} height={dimensions.height}>
                    <Layer>
                      {rectangles.map((rect, index) => (
                        <Rect
                          key={index}
                          x={rect.x}
                          y={rect.y}
                          width={rect.width}
                          height={rect.height}
                          fill={rect.fill}
                          draggable
                          onDragEnd={(e) => handleDragEnd(e, index)}
                        />
                      ))}
                    </Layer>
                    <Layer>
                      {textElements.map((element, index) => (
                        <React.Fragment>
                          <Text
                            key={index}
                            x={element.x}
                            y={element.y}
                            text={element.text}
                            fontSize={element.fontSize}
                            fill={element.fill}
                            draggable
                            onClick={() => handleTextClick(index)}
                            onDragMove={(e) => handleTextDrag(index, e)}
                            onMouseDown={(e) => {
                              e.cancelBubble = true;
                            }}
                          />
                          {editingIndex === index && (
                            <Rect
                              x={element.x}
                              y={element.y - 10}
                              width={
                                element.text.length * element.fontSize * 0.5
                              }
                              height={element.fontSize + 10}
                              fill="white"
                              draggable={false}
                            />
                          )}
                        </React.Fragment>
                      ))}
                    </Layer>
                  </Stage>
                </div>
              </div>
              <div>
                <button onClick={loadDataHandler}>Load</button>
              </div>
            </div>
          }
        />
      </Routes>
    </>
  );
}

export default App;
