import React, { useState, useRef, useEffect } from "react";
import { Stage, Layer, Rect, Text, Image } from "react-konva";
import { useNavigate } from "react-router";
import { getDownloadURL, ref } from "firebase/storage";
import { storage } from "../firebase";
import Video from "../components/Video";
import TextElements from "../components/TextElements";

const Edit = ({ fileName, uploadedFile }) => {
  const navigate = useNavigate();
  const containerRef = useRef();

  const [tool, setTool] = useState("rect");

  const [videoURL, setVideoURL] = useState(null);
  const [downloadUrl, setDownloadUrl] = useState(null);

  const [dimensions, setDimensions] = useState({
    width: 0,
    height: 0,
  });
  const [rectangles, setRectangles] = useState([]);
  const [textElements, setTextElements] = useState([]);
  const [selectedText, setSelectedText] = useState(null);

  const [drawing, setDrawing] = useState(false);
  const [startPos, setStartPos] = useState({ x: 0, y: 0 });
  const [endPos, setEndPos] = useState({ x: 0, y: 0 });

  const handleDragEnd = (e, index) => {
    const newRectangles = [...rectangles];
    newRectangles[index] = {
      ...newRectangles[index],
      x: e.target.x(),
      y: e.target.y(),
    };
    setRectangles(newRectangles);
  };

  // set dimensions
  useEffect(() => {
    const containerWidth = containerRef.current.offsetWidth;

    setDimensions({
      width: containerWidth,
      height: 600,
    });
  }, []);

  // actions
  const handleMouseDown = (e) => {
    if (tool === "rect") {
      setDrawing(true);
      const stage = e.target.getStage();
      setStartPos(stage.getPointerPosition());
      setEndPos(stage.getPointerPosition());
    }
  };
  const handleMouseMove = (e) => {
    if (!drawing) return;
    const stage = e.target.getStage();
    setEndPos(stage.getPointerPosition());
  };
  const handleMouseUp = () => {
    if (!drawing) return;
    setDrawing(false);
    const newRectangle = {
      x: Math.min(startPos.x, endPos.x),
      y: Math.min(startPos.y, endPos.y),
      width: Math.abs(endPos.x - startPos.x),
      height: Math.abs(endPos.y - startPos.y),
      fill: "blue", // Rectangle fill color
    };
    setRectangles([...rectangles, newRectangle]);
  };

  // save  to json file
  const saveShapesHandler = () => {
    const shapes = { rectangles, downloadUrl: downloadUrl };
    fetch("http://localhost:3000/shapes", {
      method: "POST",
      body: JSON.stringify(shapes),
      headers: { "Content-type": "application/json" },
    })
      .then((res) => res.json())
      .then((data) => console.log(data));
  };

  //getting video for preview
  React.useEffect(() => {
    buttonClickHandler();
    if (fileName) {
      const videoBlob = new Blob([fileName]);
      const videoObjectURL = URL.createObjectURL(videoBlob);
      setVideoURL(videoObjectURL);
    }
  }, [fileName]);

  //Download uploaded video link
  const buttonClickHandler = () => {
    if (uploadedFile === null) return;
    const fileRef = ref(storage, uploadedFile.name);

    getDownloadURL(fileRef)
      .then((downloadURL) => {
        // You can now use the downloadURL to access the uploaded video
        console.log("Download URL:", downloadURL);
        setDownloadUrl(downloadURL);
      })
      .catch((error) => {
        console.error("Error getting download URL:", error);
      });
  };

  const handleCanvasClick = (e) => {
    // Check if the click is on a text element, if yes, enable editing
    const clickedText = e.target;
    if (clickedText instanceof window.Konva.Text) {
      setSelectedText(clickedText);
    } else {
      // Otherwise, create a new text element
      const newText = (
        <Text
          key={textElements.length}
          x={
            e.evt.clientX -
            e.target.getStage().container().getBoundingClientRect().left
          }
          y={
            e.evt.clientY -
            e.target.getStage().container().getBoundingClientRect().top
          }
          text="Click to edit"
          draggable
          onDblClick={handleTextChange}
          onClick={() => {
            setSelectedText(newText);
          }}
          onTransform={() => setSelectedText(newText)}
          onTap={() => setSelectedText(newText)}
        />
      );
      setTextElements([...textElements, newText]);
    }
  };

  const handleTextChange = (e) => {
    // Update the text content when editing
    const newText = selectedText.clone({ text: e.target.value });
    console.log(newText);
    const updatedTextElements = textElements.map((element) => {
      return element.key === selectedText.key ? newText : element;
    });
    setTextElements(updatedTextElements);
  };

  return (
    <div className="App">
      <div className="global-container">
        <div className="canvas-container" ref={containerRef}>
          <Stage
            onMouseDown={handleMouseDown}
            onMouseMove={handleMouseMove}
            onMouseUp={handleMouseUp}
            width={dimensions.width}
            height={dimensions.height}
            className="canvas"
            onClick={handleCanvasClick}
          >
            <Video src={videoURL} containerRef={containerRef} />
            <Layer>
              {rectangles.map((rect, index) => (
                <Rect
                  key={index}
                  x={rect.x}
                  y={rect.y}
                  width={rect.width}
                  height={rect.height}
                  fill={rect.fill}
                  draggable
                  onDragEnd={(e) => handleDragEnd(e, index)}
                />
              ))}
            </Layer>
            <Layer>
              {drawing && (
                <Rect
                  x={Math.min(startPos.x, endPos.x)}
                  y={Math.min(startPos.y, endPos.y)}
                  width={Math.abs(endPos.x - startPos.x)}
                  height={Math.abs(endPos.y - startPos.y)}
                  stroke="blue"
                />
              )}
            </Layer>
            <Layer>{textElements.map((textElement) => textElement)}</Layer>
          </Stage>
        </div>
      </div>
      <div>
        <button onClick={() => setTool("rect")}>Rectangle</button>
        <button onClick={saveShapesHandler}>Save</button>
        <button onClick={() => navigate("/preview")}>Preview</button>
      </div>
    </div>
  );
};

export default Edit;
